#include "string.h"#include <cassert>void TestEmpty(){    String str;    assert(str.Empty());}void TestStringAcceptsArg(){    std::string arg = "Hello";    String str(arg);    assert(!str.Empty());}void TestExceptionThrownForBigString(){    bool exceptionThrown = false;    try    {        std::string arg = "HelloHelloHelloHello";        String str(arg);    }    catch(std::runtime_error&)    {        exceptionThrown = true;    }    assert(exceptionThrown);}int main(){#ifdef NDEBUG#error "This file needs to be built with Debug flags"#endif    TestEmpty();    TestStringAcceptsArg();    TestExceptionThrownForBigString();    return 0;}